import { useNavigate } from "react-router-dom";
import { useState } from "react";
import { Link } from "react-router-dom";
import styles from "./Login.module.css";

function Register({ setUsername }) {
  const [name, setName] = useState("");
  const [password, setPassword] = useState("");
  const [user, setUser] = useState({ name: "", password: "",first_name:"" }); // מערך שמכיל את המשתנים של השם והסיסמה

  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!name || !password) {
      return;
    }

    const url = "http://localhost:3000/register";
    const requestOptions = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ name, password }),
    };

    fetch(url, requestOptions)
      .then((response) => {
        if (response.status === 200) {
          return response.json();
        } else if (response.status === 409) {
          throw "Username or password already exists";
        }
      })
      .then((user) => {
        localStorage.setItem("currentUser", JSON.stringify(user));
        setUsername(name);
        navigate(`/users/${name}/info`);
      })
      .catch((error) => {
        console.error(error);
        alert(error);
      });
  };

  return (
    <section className={styles.section}>
      <form className={styles.form} onSubmit={handleSubmit}>
        <h5>REGISTER</h5>
        <div className={styles["form-row"]}>
          <input
            type="text"
            placeholder="Username"
            className={styles["form-input"]}
            id="name"
            value={user.name}
            onChange={(e) => setUser({ ...user, name: e.target.value })} // עדכון השדה של השם ב-user
          />
        </div>
        <div className={styles["form-row"]}>
          <input
            type="password"
            placeholder="Password"
            className={styles["form-input"]}
            id="password"
            value={user.password}
            onChange={(e) => setUser({ ...user, password: e.target.value })} // עדכון השדה של השם ב-user
          />
        </div>
        <div className={styles["form-row"]}>
          <input
            type="text"
            placeholder="first_name"
            className={styles["form-input"]}
            id="first_name"
            value={user.first_name}
            onChange={(e) => setUser({ ...user, first_name: e.target.value })} // עדכון השדה של השם ב-user
          />
        </div>
        <button type="submit" className={styles.btn}>
          REGISTER
        </button>
        <Link className={styles["btn-link"]} to="/login">
          LOGIN
        </Link>
      </form>
    </section>
  );
}

export default Register;
