import { useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import styles from "./Todos.module.css";

function Todos() {
  const [user, setUser] = useState([]);
  const [Todos, setTodos] = useState([]);

  const [sortedBy, setSortedBy] = useState("random");
  const navigate = useNavigate();
  // .then((data) =>
  //setAlbums(data.filter((album) => album.userId === user.id))
  //)
  useEffect(() => {
    console.log("useEffect");
    const objectData = JSON.parse(localStorage.getItem('currentUser'));
    if (objectData) {
      setUser(objectData);
    }
    if(user) {
        fetch(`http://localhost:3000/todos/${user.id}`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                name: "bat7",
                password: "1234"
            })
        })
            .then(response => {
                if (response.ok) {
                    console.log(response.status); // הדפסת הסטטוס
                    return response.json(); // מחזיר פרומיס עם תוכן התשובה בפורמט JSON
                } else {
                    throw new Error('Error: ' + response.status); // זריקת שגיאה אם יש שגיאה בתגובה
                }
            })
            .then(data => {
                console.log(data); // הדפסת האובייקט המלא
                data.map(todo => {
                  console.log(todo.userId);
                  console.log(todo.id);
                  console.log(todo.title);
                  // ניתן להדפיס כל שדה נוסף באובייקט לפי הצורך
                });
                setTodos(data);
              })
            .catch(error => {
                console.error(error);
            });
    }
}, []);

  // const currentUser = JSON.parse(localStorage.getItem("currentUser"));

  const handleChangeTodo = (todoId) => {
    const updatedTodos = todos.map((todo) => {
      if (todo.id === todoId) {
        return { ...todo, completed: !todo.completed };
      } else {
        return todo;
      }
    });
    setTodos(updatedTodos);
    localStorage.setItem("todos", JSON.stringify(updatedTodos));
  };

  const notCompletedTodos = todos.filter((todo) => !todo.completed);

  const completedTodos = todos.filter((todo) => todo.completed);

  const sortTodos = (event) => {
    const sortBy = event.target.value;
    setSortedBy(sortBy);
  };

  const sortAndFilterTodos = (todoList) => {
    switch (sortedBy) {
      case "random":
        return todoList.sort(() => Math.random() - 0.5);
      case "alphabetical":
        return todoList.sort((a, b) => a.title.localeCompare(b.title));
      case "by id":
        return todoList.sort((a, b) => a.id - b.id);
      default:
        return todoList;
    }
  };
  const handlerChange = async (id, complete) => {//לעדכן את החיווי
    try {
        const response = await fetch(`/todos/${id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(id, complete),
        });

        if (response.ok) {
            console.log('Todo deleted successfully');
            // כאן ניתן לבצע פעולות נוספות אחרי מחיקת המשימה
        } else {
            throw new Error('Failed to delete todo');
        }
    } catch (error) {
        console.error('Failed to delete todo:', error.message);
    }
}
// פונקציה שמבצעת בקשת מחיקה לשרת
const handleDelete = async (id) => {
    try {
        const response = await fetch(`/todos/${id}`, {
            method: 'DELETE',
        });

        if (response.ok) {
            console.log('Todo deleted successfully');
            // כאן ניתן לבצע פעולות נוספות אחרי מחיקת המשימה
        } else {
            throw new Error('Failed to delete todo');
        }
    } catch (error) {
        console.error('Failed to delete todo:', error.message);
    }
};
const handleUpdate = async (id, title) => {
    try {
        const response = await fetch(`/todos/${id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(id, title),
        });

        if (response.ok) {
            console.log('Todo deleted successfully');
            // כאן ניתן לבצע פעולות נוספות אחרי מחיקת המשימה
        } else {
            throw new Error('Failed to delete todo');
        }
    } catch (error) {
        console.error('Failed to delete todo:', error.message);
    }
};
  return (
    <section className={styles["todos-select"]}>
      {/* <div>
        Sort by:
        <select value={sortedBy} onChange={sortTodos}>
          <option value="by id">By id</option>
          <option value="alphabetical">Alphabetical</option>
          <option value="random">Random</option>
        </select>
      </div> */}
      <br />
      <br />

      <div className={styles["lists"]}>
        <div className={styles["todos-list"]}>

          <ul className="todo-items">
            {userTodos.map(todos => (
              <li key={todos.userId}>
                <input
                  type="checkbox"
                  checked={todos.completed}
                  onChange={() => handlerChange(todos.id, todos.completed)}
                />
                <span className={todos.completed ? '' : 'completed'}>{todos.title}</span>
                <button onClick={() => handleDelete(todos.id)}>מחיקה</button>
                <button onClick={() => handleUpdate(todos.id, todos.title)}>עידכון</button>
                    // לעדכן את התוכן

              </li>
            ))}
          </ul>
          {/* כפתור הוספה */}
          {/* <h4>Not Completed</h4>
          {sortAndFilterTodos(notCompletedTodos).map((todo) => (
            <div className={styles["todos-item"]} key={todo.id}>
              <div className={styles["todo-case"]}>
                <input
                  className={styles["todos-checkbox"]}
                  type="checkbox"
                  checked={todo.completed}
                  onChange={() => handleChangeTodo(todo.id)}
                />
                <label className={styles["todos-label"]}>{todo.title}</label>
              </div>
            </div>
          ))}
        </div>
        <div className={styles["todos-list"]}>
          <h4>Completed</h4>
          {sortAndFilterTodos(completedTodos).map((todo) => (
            <div className={styles["todos-item"]} key={todo.id}>
              <div className={styles["todo-case"]}>
                <input
                  className={styles["todos-checkbox"]}
                  type="checkbox"
                  checked={todo.completed}
                  onChange={() => handleChangeTodo(todo.id)}
                />
                <label className={styles["todos-label"]}>{todo.title}</label>
              </div>
            </div>
          ))} */}
        </div>
      </div>
    </section>
  );
}

export default Todos;
