import { useNavigate } from "react-router-dom";
import { useParams, useEffect, useState } from "react";
import { FaTrash, FaEdit, FaSave } from 'react-icons/fa';

import styles from "./Todos.module.css";

function Todos() {
  const [user, setUser] = useState([]);
  const [Todos, setTodos] = useState([]);
  const [editingTodoId, setEditingTodoId] = useState(null);
  const [editTitle, setEditTitle] = useState(null);
  const [editCheck, setEditCheck] = useState(null);



  const navigate = useNavigate();

  const usern = JSON.parse(localStorage.getItem('currentUser'));
  useEffect(() => {
    if (Todos.length === 0) {
      // טעינת הנתונים כאן
      fetchTodos();
    }
  }, []);

  const fetchTodos = async () => {
    try {
      const response = await fetch(`http://localhost:3000/todos/${usern.id}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json'
        },

      })
      // .then(response => {
      if (response.ok) {
        console.log(response.status); // הדפסת הסטטוס
        const newData = await response.json(); // מחזיר פרומיס עם תוכן התשובה בפורמט JSON
        console.log(newData);
        setTodos(newData);
        console.log(Todos);
        // } else {
        //   throw new Error('Error: ' + response.status); // זריקת שגיאה אם יש שגיאה בתגובה
        // }
      }

    } catch (error) {
      console.error('Failed to fetch todos:', error.message);
    }

  };
  // פונקציה שמבצעת בקשת מחיקה לשרת
  const handleDelete = async (id) => {
    try {
      // setTodos(prevTodos => prevTodos.filter(todo => todo.id !== id)); // עדכון התצוגה לאחר מחיקת המשימה

      const response = await fetch(`http://localhost:3000/todos/delete/${id}`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json'
        },
      });

      if (response.ok) {
        console.log('Todo deleted successfully');
        // fetchTodos();
        setTodos(prevTodos => prevTodos.filter(todo => todo.id !== id)); // עדכון התצוגה לאחר מחיקת המשימה
      } else {
        throw new Error('Failed to delete todo');
      }
    } catch (error) {
      console.error('Failed to delete todo:', error.message);
    }

  };
  const handleUpdate = (id, compn) => {
    console.log(Todos);
    setTodos(prevTodos => {
      const updatedTodos = prevTodos.map(todo => {
        if (todo.id === id) {
          return { ...todo, completed: compn };
        }
        return todo;
      });
      console.log(updatedTodos);
      return updatedTodos;
    });
  };

  const editData = async (url, data, setData) => {
    const requestOptions = {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    };

    try {
      const response = await fetch(url, requestOptions);
      if (response.status === 200) {
        const updatedRecord = await response.json();
        setData((prevData) => {
          return prevData.map((record) => {
            if (record.id === updatedRecord.id) {
              return updatedRecord;
            }
            return record;
          });
        });
      }
    } catch (error) {
      console.error("Error updating data:", error);
      navigate("/error");
    }
  };
  const handleSave = async () => {
    editData(`http://localhost:3000/todos/update-title/${editingTodoId}`,{ title: editTitle},setTodos)
    // if (editingTodoId != null) {
    //   setTodos(prevTodos => {
    //     return prevTodos.map(todo => {
    //       if (todo.id === editingTodoId) {
    //         return { ...todo, title: editTitle };
    //       }
    //       return todo;
    //     });
    //   });

    //   try {
    //     const response = await fetch(`http://localhost:3000/todos/update-title/${editingTodoId}`, {
    //       method: 'PUT',
    //       headers: {
    //         'Content-Type': 'application/json'
    //       },
    //       body: JSON.stringify({
    //         title: editTitle
    //       })
    //     });
    //     if (response.ok) {
    //       const updatedTodo = await response.json();
    //       console.log(updatedTodo);
    //       fetchTodos();

    //       // עדכון המשימה בסטור או מסד הנתונים
    //     } else {
    //       throw new Error('Error: ' + response.status);
    //     }
    //   } catch (error) {
    //     console.error(error);
    //   }
    // }
  }
  const update = async (id, completed) => {
    await setTodos(prevTodos => {
      return prevTodos.map(todo => {
        if (todo.id === id) {
          return { ...todo, completed: completed };
        }
        return todo;
      });
    });
  }
  const handleCheckChange = async (id, completed) => {
    // update();
    setTodos(prevTodos => {
      return prevTodos.map(todo => {
        if (todo.id === id) {
          return { ...todo, completed: completed };
        }
        return todo;
      });
    });

    console.log(id, completed);
    try {
      const response = await fetch(`http://localhost:3000/todos/update-completed/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          completed: completed,
          // co: editTitle
        })
      });
      if (response.ok) {

        // fetchTodos();

        console.log(response);
        console.log(Todos);
        // handleUpdate(id, completed)
        console.log(Todos);

        // עדכון המשימה בסטור או מסד הנתונים
      } else {
        throw new Error('Error: ' + response.status);
      }
    } catch (error) {
      console.error(error);
    }
  }
  const handleEdit = (id) => {
    setEditingTodoId(id);
  };
  const [newTodoTitle, setNewTodoTitle] = useState('');

  const addTodo = () => {
    // let title = document.getElementById("title").value;
    // let body = document.getElementById("body").value;
    console.log(newTodoTitle);
    if (newTodoTitle.trim() !== '') {

      const url = "http://localhost:3000/todos";
      const todo = {
        userId: usern.id,
        title: newTodoTitle,
        completed: 0,
      };
      const requestOptions = {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(todo),
      };

      fetch(url, requestOptions)
        .then((response) => {
          if (response.status === 200) {
            console.log(response.json);
            const newTodo = response.json();
            setTodos(prevTodos => [...prevTodos, newTodo]);
            setNewTodoTitle('');

          }
        })
        .catch((error) => {
          console.error("Error adding new post:", error);
          navigate("/error");
        });
    }
    else {
      console.log("the title cant be blank");
    }
  };
  return (

    <section className={styles["todos-select"]}>
      <div className={styles["lists"]}>

        <div className={styles["todos-list"]}>
          <input
            type="text"
            value={newTodoTitle}
            onChange={e => setNewTodoTitle(e.target.value)}
          />
          <button onClick={addTodo}>Add Todo</button>
          {Todos.map(todo => (
            <div className={styles["todos-item"]} key={todo.id}>
              <div className={styles["todo-case"]}>
                <input
                  className={styles["todos-checkbox"]}
                  type="checkbox"
                  checked={todo.completed}
                  // onChange={(e) => setEditCheck(!e.target.value)}
                  onChange={() => handleCheckChange(todo.id, !todo.completed)}
                />
                <span className={todo.completed ? '' : 'completed'}>
                  {editingTodoId === todo.id ? (
                    <input
                      className={styles["todos-input"]}
                      type="text"
                      value={editTitle}
                      onChange={(e) => setEditTitle(e.target.value)}
                    />
                  ) : (
                    todo.title
                  )}
                </span>
                {/* {todos.title}</span> */}
                <button onClick={() => handleDelete(todo.id)}>
                  <FaTrash />
                </button>
                <button onClick={() => handleEdit(todo.id)}>
                  <FaEdit />
                </button>
                <button onClick={() => handleSave()}>
                  <FaSave />
                </button>
              </div>
            </div>
          ))}
          {/* כפתור הוספה */}
          {/* <h4>Not Completed</h4>
          {sortAndFilterTodos(notCompletedTodos).map((todo) => (
            <div className={styles["todos-item"]} key={todo.id}>
              <div className={styles["todo-case"]}>
                <input
                  className={styles["todos-checkbox"]}
                  type="checkbox"
                  checked={todo.completed}
                  onChange={() => handleChangeTodo(todo.id)}
                />
                <label className={styles["todos-label"]}>{todo.title}</label>
              </div>
            </div>
          ))}
        </div>
        <div className={styles["todos-list"]}>
          <h4>Completed</h4>
          {sortAndFilterTodos(completedTodos).map((todo) => (
            <div className={styles["todos-item"]} key={todo.id}>
              <div className={styles["todo-case"]}>
                <input
                  className={styles["todos-checkbox"]}
                  type="checkbox"
                  checked={todo.completed}
                  onChange={() => handleChangeTodo(todo.id)}
                />
                <label className={styles["todos-label"]}>{todo.title}</label>
              </div>
            </div>
          ))} */}
        </div>
      </div>
    </section>
  );
}

export default Todos;
