import { useNavigate } from "react-router-dom";
import { useParams, useEffect, useState } from "react";
import { FaTrash, FaEdit, FaSave } from 'react-icons/fa';
import styles from "../Login.module.css";
// import {} '../Login.'

function CreateNewBook(book_name) {
    const [isFieldEnabled, setIsFieldEnabled] = useState(false);
    const [user, setUser] = useState(JSON.parse(localStorage.getItem("currentUser")));
    const [newbook, setBook] = useState({
        book_name: book_name,
        author_name: "",
        categories: [],
        publication_year: "",
        owner_code: user.id,
        availability: false,
        borrower_username: ""
    });
    const [categories, setCategories] = useState([]);

    useEffect(() => {
        const url = "http://localhost:3000/category";
        const requestOptions = {
            method: "GET",
            headers: {
                "Content-Type": "application/json",
            }
        };
        fetch(url, requestOptions)
            .then((response) => {
                console.log(response.json().Array[0]);
                if (response.status === 202) {
                    console.log(response.json());
                    return response.json();
                } else
                    // if (response.status === 409) {
                    
                    throw "Username or password already exists";
                //  }
            })
            .then((u) => {
                console.log(u);
                console.log("the book added");
                setCategories(u[0]);
            })
            .catch((error) => {
                console.error(error);
                alert(error);
            });
    }, [categories]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        console.log("handleSubmit");
        const url = "http://localhost:3000/book/volumes";
        const requestOptions = {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({ book_id: newbook.book_id, owner_code: newbook.owner_code }),
        };

        fetch(url, requestOptions)
            .then((response) => {
                if (response.status === 202) {
                    console.log(response);
                    return response.json();
                } else
                    // if (response.status === 409) {
                    throw "Username or password already exists";
                //  }
            })
            .then((u) => {
                console.log(u);
                console.log("the book added");
                // localStorage.setItem("currentUser", JSON.stringify(user));
                // navigate(`/users/${user.username}/info`);
            })
            .catch((error) => {
                console.error(error);
                alert(error);
            });
    };
    const handleCategoryChange = (e) => {
        const categoryName = e.target.value;
        setBook({ ...newbook, category_name: categoryName });
      };
    return (
        <section className={styles.section}>
            <form >
             {/* className={styles.form} > */}
                {/* onSubmit={handleSubmit}> */}
                <h5>new book deatiles</h5>
                <div className={styles["form-row"]}>
                    <input
                        type="text"
                        placeholder="author_name"
                        className={styles["form-input"]}
                        id="author_name"
                        value={newbook.author_name}
                        onChange={(e) => setBook({ ...newbook, author_name: e.target.value })} // עדכון השדה של השם ב-user
                    />
                </div>
                <div className={styles["form-row"]}>
                    <input
                        type="year"
                        placeholder="publication_year"
                        className={styles["form-input"]}
                        id="publication_year"
                        value={newbook.publication_year}
                        onChange={(e) => setBook({ ...newbook, publication_year: e.target.value })} // עדכון השדה של השם ב-user
                    />
                </div>
                <div className={styles["form-row"]}>
                    {categories.map((category, index) => (
                        <div key={index}>
                            <input
                                type="checkbox"
                                id={`category_${index}`}
                                value={category}
                                checked={newbook.category_name === category}
                                onChange={handleCategoryChange}
                            />
                            <label htmlFor={`category_${index}`}>{category}</label>
                        </div>
                    ))}
                </div>

            </form>
        </section>
    );
}


export default CreateNewBook;
