import { useNavigate } from "react-router-dom";
import { useParams, useEffect, useState } from "react";

import styles from "./Todos.module.css";

function Todos() {
  const [user, setUser] = useState([]);
  const [Todos, setTodos] = useState([]);
  const [editingTodoId, setEditingTodoId] = useState(null);
  const [editTitle, setEditTitle] = useState(null);
  const [editCheck, setEditCheck] = useState(null);



  const [sortedBy, setSortedBy] = useState("random");
  const navigate = useNavigate();
  // .then((data) =>
  //setAlbums(data.filter((album) => album.userId === user.id))
  //)
  const func = async () => {
    const user = JSON.parse(localStorage.getItem('currentUser'));

    if (user) {
      await setUser(user);
    }
  }
  const usern = JSON.parse(localStorage.getItem('currentUser'));


  useEffect(() => {
    // console.log(username);

    console.log(user);
    // if (user) {
    console.log(user.id);
    fetch(`http://localhost:3000/todos/${usern.id}`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json'
      },

    })
      .then(response => {
        if (response.ok) {
          console.log(response.status); // הדפסת הסטטוס
          return response.json(); // מחזיר פרומיס עם תוכן התשובה בפורמט JSON
        } else {
          throw new Error('Error: ' + response.status); // זריקת שגיאה אם יש שגיאה בתגובה
        }
      })
      .then(data => {
        console.log(data); // הדפסת האובייקט המלא
        setTodos(data);
      })
      .catch(error => {
        console.error(error);
      });
    // }
  }, []);

  // פונקציה שמבצעת בקשת מחיקה לשרת
  const handleDelete = async (id) => {
    // try {
    //     const response = await fetch(`/todos/${id}`, {
    //         method: 'DELETE',
    //     });

    //     if (response.ok) {
    //         console.log('Todo deleted successfully');
    //         // כאן ניתן לבצע פעולות נוספות אחרי מחיקת המשימה
    //     } else {
    //         throw new Error('Failed to delete todo');
    //     }
    // } catch (error) {
    //     console.error('Failed to delete todo:', error.message);
    // }
  };

  const handleSave = async () => {
    if (editingTodoId != null) {
      try {
        const response = await fetch(`http://localhost:3000/todos/${editingTodoId}`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            completed: editCheck,
            title: editTitle
          })
        });
        if (response.ok) {
          const updatedTodo = await response.json();
          console.log(updatedTodo);

          // עדכון המשימה בסטור או מסד הנתונים
        } else {
          throw new Error('Error: ' + response.status);
        }
      } catch (error) {
        console.error(error);
      }
    }
  }


  const handleEdit = (id) => {
    setEditingTodoId(id);
  };
  return (
    <section className={styles["todos-select"]}>
      <div className={styles["lists"]}>
        <div className={styles["todos-list"]}>
          {Todos.map(todos => (
            <div className={styles["todos-item"]} key={todos.id}>
              <div className={styles["todo-case"]}>
                <input
                  className={styles["todos-checkbox"]}
                  type="checkbox"
                  checked={todos.completed}
                  onChange={(e) => setEditCheck(!e.target.value)}
                // onChange={() => handleCheckChange(todos.id, !todos.completed)}
                />
                <span className={todos.completed ? '' : 'completed'}>
                  {editingTodoId === todos.id ? (
                    <input
                      className={styles["todos-input"]}
                      type="text"
                      value={editTitle}
                      onChange={(e) => setEditTitle(e.target.value)}
                    />
                  ) : (
                    todos.title
                  )}
                </span>
                {/* {todos.title}</span> */}
                <button onClick={() => handleDelete(todos.id)}>DELETE</button>
                <button onClick={() => handleEdit(todos.id)}>EDIT</button>
                <button onClick={() => handleSave()}>SAVE</button>
              </div>
            </div>
          ))}
          {/* כפתור הוספה */}
          {/* <h4>Not Completed</h4>
          {sortAndFilterTodos(notCompletedTodos).map((todo) => (
            <div className={styles["todos-item"]} key={todo.id}>
              <div className={styles["todo-case"]}>
                <input
                  className={styles["todos-checkbox"]}
                  type="checkbox"
                  checked={todo.completed}
                  onChange={() => handleChangeTodo(todo.id)}
                />
                <label className={styles["todos-label"]}>{todo.title}</label>
              </div>
            </div>
          ))}
        </div>
        <div className={styles["todos-list"]}>
          <h4>Completed</h4>
          {sortAndFilterTodos(completedTodos).map((todo) => (
            <div className={styles["todos-item"]} key={todo.id}>
              <div className={styles["todo-case"]}>
                <input
                  className={styles["todos-checkbox"]}
                  type="checkbox"
                  checked={todo.completed}
                  onChange={() => handleChangeTodo(todo.id)}
                />
                <label className={styles["todos-label"]}>{todo.title}</label>
              </div>
            </div>
          ))} */}
        </div>
      </div>
    </section>
  );
}

export default Todos;
